  clipboard_text_received: json({
    req_id: string(),
    data: (value) => (value === null ? value : string().optional().parse(value)),
  }),
  custom_method_invoked: json({
    req_id: string(),
    result: (value) => value,
    error: string().optional(),
  }),
  popup_closed: {
    parse(value) {
      return json({
        button_id: (value) => (
          value === null || value === undefined
            ? undefined
            : string().parse(value)
