import axios from 'axios';

// API базовый URL
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000/api/v1';

// Создаем инстанс axios с базовыми настройками
const api = axios.create({
    baseURL: API_BASE_URL,
    headers: {
        'Content-Type': 'application/json',
    },
});

// Добавляем интерцептор для авторизации
api.interceptors.request.use((config) => {
    // Получаем токен из localStorage
    const token = localStorage.getItem('token');

    // Если токен есть, добавляем его в заголовок
    if (token) {
        config.headers.Authorization = `Bearer ${token}`;
    } else {
        // Иначе используем Telegram Mini App данные
        const initData = localStorage.getItem('telegram_init_data');
        if (initData) {
            config.headers['X-Telegram-Init-Data'] = initData;
        }
    }

    return config;
});

// API методы для авторизации
export const authAPI = {
    // Инициализация через Telegram
    telegramAuth: async () => {
        const initData = localStorage.getItem('telegram_init_data');
        if (!initData) {
            throw new Error('No Telegram init data available');
        }
        const response = await api.post('/auth/telegram', { init_data: initData });
        if (response.data.token) {
            localStorage.setItem('token', response.data.token);
        }
        return response.data;
    },

    // Проверка токена
    verifyAuth: async (token) => {
        return api.post('/auth/verify', { token });
    },

    // Выход
    logout: async () => {
        const token = localStorage.getItem('token');
        if (token) {
            await api.post('/auth/logout', { token });
            localStorage.removeItem('token');
        }
    }
};

// API методы для пользователя
export const userAPI = {
    // Получение текущего пользователя
    getCurrentUser: async () => {
        return api.get('/users/me');
    },

    // Получение баланса
    getBalance: async () => {
        return api.get('/users/balance');
    },

    // Запрос на вывод средств
    requestWithdraw: async (amount, currency) => {
        return api.post('/users/withdraw', { amount, currency });
    },

    // История выводов
    getWithdrawHistory: async () => {
        return api.get('/users/withdrawals');
    }
};

// API методы для игр
export const gameAPI = {
    // Получение всех игр
    getAllGames: async () => {
        return api.get('/games');
    },

    // Получение игр пользователя
    getMyGames: async () => {
        return api.get('/games/my');
    },

    // Получение игры по ID
    getGame: async (id) => {
        return api.get(`/games/${id}`);
    },

    // Создание игры
    createGame: async (gameData) => {
        return api.post('/games', gameData);
    },

    // Удаление игры
    deleteGame: async (id) => {
        return api.delete(`/games/${id}`);
    },

    // Поиск игр
    searchGames: async (params) => {
        return api.get('/games/search', { params });
    },

    // Пополнение reward pool
    addToRewardPool: async (id, amount) => {
        return api.post(`/games/${id}/reward`, { amount });
    },

    // Активация игры
    activateGame: async (id) => {
        return api.post(`/games/${id}/activate`);
    },

    // Деактивация игры
    deactivateGame: async (id) => {
        return api.post(`/games/${id}/deactivate`);
    }
};

// API методы для лобби
export const lobbyAPI = {
    // Присоединение к игре
    joinGame: async (gameId, betAmount) => {
        return api.post('/lobbies', { game_id: gameId, bet_amount: betAmount });
    },

    // Получение лобби по ID
    getLobby: async (id) => {
        return api.get(`/lobbies/${id}`);
    },

    // Получение активного лобби
    getActiveLobby: async () => {
        return api.get('/lobbies/active');
    },

    // Получение всех лобби пользователя
    getUserLobbies: async () => {
        return api.get('/lobbies');
    },

    // Отправка попытки
    makeAttempt: async (lobbyId, word) => {
        return api.post(`/lobbies/${lobbyId}/attempt`, { word });
    },

    // Получение попыток
    getAttempts: async (lobbyId) => {
        return api.get(`/lobbies/${lobbyId}/attempts`);
    },

    // Продление времени лобби
    extendLobbyTime: async (lobbyId) => {
        return api.post(`/lobbies/${lobbyId}/extend`);
    }
};

// API методы для транзакций
export const transactionAPI = {
    // Получение транзакций пользователя
    getUserTransactions: async () => {
        return api.get('/transactions');
    },

    // Получение транзакции по ID
    getTransaction: async (id) => {
        return api.get(`/transactions/${id}`);
    },

    // Создание депозита
    createDeposit: async (amount, currency) => {
        return api.post('/transactions/deposit', { amount, currency });
    },

    // Проверка депозита
    verifyDeposit: async (txHash) => {
        return api.post('/transactions/verify', { tx_hash: txHash });
    },

    // Получение статистики транзакций
    getTransactionStats: async () => {
        return api.get('/transactions/stats');
    }
};

export default api;
